function balls(input) {

    let counter = 0;

    let blackBall = 0;

    let redCounter = 0;
    let orangeCounter = 0;
    let yellowCounter = 0;
    let whiteCounter = 0;
    let total = 0;
    for (let index = 1; index < input.length; index++) {

        let currentColour = input[index];

        switch (currentColour) {
            case 'red':
                total += 5;
                redCounter++;
                break;
            case 'orange':
                total += 10;
                orangeCounter++;
                break;
            case 'yellow':
                total += 15;
                yellowCounter++;
                break;
            case 'white':
                total += 20;
                whiteCounter++;
                break;
            case 'black':
                blackBall += 1;
                total = Math.floor(total / 2);
                break;
            default:
                counter++;
                break
        }

    }

    if (blackBall >= 1) {
        console.log(`Total points: ${total}`);
        console.log(`Red balls: ${redCounter}`);
        console.log(`Orange balls: ${orangeCounter}`);
        console.log(`Yellow balls: ${yellowCounter}`);
        console.log(`White balls: ${whiteCounter}`);
        console.log(`Other colors picked: ${counter}`);
        console.log(`Divides from black balls: ${blackBall}`);

    } else {
        console.log(`Total points: ${total}`);
        console.log(`Red balls: ${redCounter}`);
        console.log(`Orange balls: ${orangeCounter}`);
        console.log(`Yellow balls: ${yellowCounter}`);
        console.log(`White balls: ${whiteCounter}`);
        console.log(`Other colors picked: ${counter}`);
        console.log(`Divides from black balls: ${blackBall}`);

    }
}

