using System;

class Program
{
	static void Main()
	{
		const ConsoleKey CommandAddDossier = ConsoleKey.F1;
		const ConsoleKey CommandShowDossiers = ConsoleKey.F2;
		const ConsoleKey CommandDeleteDossier = ConsoleKey.F3;
		const ConsoleKey CommandExit = ConsoleKey.F4;

		List<string> dossiers = new List<string>();

		ConsoleKey userInput;
		bool isContinue = true;

		while (isContinue)
		{
			Console.Clear();
			Console.WriteLine($"{CommandAddDossier} - Добавить досье.");
			Console.WriteLine($"{CommandShowDossiers} - Вывести все досье.");
			Console.WriteLine($"{CommandDeleteDossier} - Удалить досье.");
			Console.WriteLine($"{CommandExit} - Выход.");
			userInput = Console.ReadKey().Key;

			switch (userInput)
			{
				case CommandAddDossier:
					AddDossier(dossiers);
					break;

				case CommandShowDossiers:
					ShowDossiers(dossiers);
					Console.ReadKey();
					break;

				case CommandDeleteDossier:
					TryDeleteDossier(dossiers);
					break;

				case CommandExit:
					isContinue = false;
					break;

				default:
					Console.WriteLine("\nНе корректен ввод.");
					Console.ReadKey();
					break;
			}
		}
	}

	static void AddDossier(List<string> dossiers)
	{
		string dossier = string.Empty;

		Console.Clear();

		dossier = AddItem(dossier, "Введите фамилию", false);
		dossier = AddItem(dossier, "Введите имя");
		dossier = AddItem(dossier, "Введите отчество");
		dossier = AddItem(dossier, "Введите должность");

		dossiers.Add(dossier);
	}

	static string AddItem(string dossier, string message, bool haveSeparator = true)
	{
		char separator = ' ';

		Console.Write($"{message}: ");

		if (haveSeparator)
			dossier += separator + Console.ReadLine();
		else
			dossier += Console.ReadLine();

		return dossier;
	}

	static void ShowDossiers(List<string> dossiers)
	{
		int surnameIndex = 0;
		int nameIndex = 1;
		int patronymicIndex = 2;
		int positionIndex = 3;

		char separator = ' ';

		Console.Clear();

		if (IsEmptyDossier(dossiers))
		{
			Console.Write("Досье пустое.");
		}
		else
		{
			for (int i = 0; i < dossiers.Count; i++)
			{
				string[] separateDossier = dossiers[i].Split(separator);

				Console.WriteLine($"{i + 1} - {separateDossier[surnameIndex]} {separateDossier[nameIndex]} " +
					$"{separateDossier[patronymicIndex]} - {separateDossier[positionIndex]}");
			}
		}
	}

	static void TryDeleteDossier(List<string> dossiers)
	{
		Console.Clear();

		if (IsEmptyDossier(dossiers))
		{
			Console.Write("Досье пустое.");
			Console.ReadKey();
		}
		else
		{
			ShowDossiers(dossiers);

			Console.Write("Введите номер удаляемого пункта досье: ");
			string userInput = Console.ReadLine();

			if (Int32.TryParse(userInput, out int number))
			{
				if (number > 0 && number <= dossiers.Count)
				{
					dossiers.RemoveAt(number - 1);

					Console.WriteLine("Досье удалено.");
				}
				else
				{
					Console.WriteLine("Такого пунrта меню нет.");
				}
			}
			else
			{
				Console.WriteLine("Введено не число.");
			}

			Console.ReadKey();
		}
	}

	static bool IsEmptyDossier(List<string> dossiers)
	{
		if (dossiers.Count <= 0 || dossiers == null)
			return true;
		else
			return false;
	}
}

