const int maxn = 3e5 + 10;
vector<int> graph[maxn];
int salary[maxn];
int parent[maxn];
int n, B;
int cnt;
void dfs(int node) {
    if(node == 0) {
        return;
    }
    for(int i = 0; i < (int) graph[node].size(); i++) {
        int neighbour = graph[node][i];
        if(neighbour == parent[node]) {
            if(salary[node] > salary[neighbour]) {
                cnt++;
                salary[neighbour] = salary[node];
                dfs(neighbour);
            }
        }
    }
}
