void solve_test() {
    int n;
    cin >> n;

    ll k;
    cin >> k;

    vi a(n);
    for (auto& v : a) cin >> v;

    auto get_ans = [&]() {
        vi basics(a.begin(), a.end());
        basics.push_back(0);
        sort(basics.begin(), basics.end());

        vi additionals(basics.begin(), basics.end());

        int neg_inf = basics.front() * 2 - 1;
        additionals.insert(additionals.begin(), neg_inf);

        int pos_inf = basics.back() * 2 + 1;
        additionals.push_back(pos_inf);

        auto calculate = [&](int min_power) {
            ll larger = 0;
            ll larger_sum = 0;

            ll suffix_sum = 0;
            for (int basic_pos = 0, min_additional_pos = additionals.size() - 1; basic_pos < basics.size(); ++basic_pos) {
                int basic_power = basics[basic_pos];

                while (min_additional_pos > 1 && basic_power + additionals[min_additional_pos - 1] > min_power) {
                    --min_additional_pos;
                    suffix_sum += additionals[min_additional_pos];
                }

                // -inf, 1, 2, 3, 4, ..., n, inf
                int suffix_larger = additionals.size() - 1 - min_additional_pos;
                larger += suffix_larger;

                larger_sum += ll(basic_power) * suffix_larger + suffix_sum;

                if (basic_pos + 1 >= min_additional_pos) {
                    // remove (i, i)
                    larger--;
                    larger_sum -= basic_power * 2;
                }
            }

            larger /= 2;
            larger_sum /= 2;

            return make_pair(larger, larger_sum);
        };

        int left = neg_inf, right = pos_inf; // larger(left) >= k > larger(right)
        while (right - left > 1) {
            int mid = (right + left) / 2;

            auto [larger, _] = calculate(mid);
            if (larger < k) {
                right = mid;
            } else {
                left = mid;
            }
        }

        auto [larger, larger_sum] = calculate(right);
        return larger_sum + (k - larger) * right;
    };

    cout << get_ans() << "\n";
}

