function onOpen() {
  var ui = SpreadsheetApp.getUi();
  ui.createMenu('Paypal Ge Taxes')
    .addItem('Calculate USD', 'calculateUSD')
    .addItem('Calculate EUR', 'calculateEUR')
    .addToUi();
