using namespace std;


class ForwardList {
public:

	struct Node { //односвязный список состоит из узлов
		int value; // узел хранит информативную часть
		Node* next; // и указатель на следующий узел в списке
	};

    void Push_front(const int& value) {
        head = new Node{value, head};
        size++;
    }

    void Pop_front() {
        if (size > 0) {
            Node* temp = head;
            head = head->next;
            delete temp;
            size--;
        }
    }

    void Print() {
        Node* temp = head;
        while (temp != nullptr) {
            cout << temp->value << " ";
            temp = temp->next;
        }
        cout << endl;
    }

    int Size() const {
        return size;
    }

private:
    int size = 0;
    Node* head = nullptr;
};

int main() {
    ForwardList list1;
    for (int i = 0; i < 10; i++) {
        list1.Push_front(i + 1);
    }
    list1.Print();
    cout << list1.Size() << endl;

    for (int i = 0; i < 3; i++) {
        list1.Pop_front();
    }
    list1.Print();
    cout << list1.Size() << endl;
}

