+			{
+				if (resultSet != null)
+				{
+					resultSet.close();
+				}
+				if (statement != null)
+				{
+					statement.close();
+				}
+				if (con != null)
+				{
+					con.close();
+				}
+			}
+			catch (SQLException e)
+			{
+				
+				e.printStackTrace();
+			}
+		}
+		
+		return recoverableItems;
+	}
+	
+	@Override
+	public int[] getItemIds()
+	{
+		return ITEM_IDS;
+	}
