package main

import (
	"fmt"
	"sync"
)

type Resource struct {
	data int
}

func (r *Resource) increment() {
	r.data++
}

type Client struct {
	id       int
	resource Resource
	mu       sync.RWMutex
}

type ActiveClients struct {
	clients []*Client
	mu      sync.RWMutex
}

func (ac *ActiveClients) add(client *Client) {
	ac.mu.Lock()
	defer ac.mu.Unlock()
	if ac.clients == nil {
		ac.clients = make([]*Client, 0)
	}
	ac.clients = append(ac.clients, client)
}

func (ac *ActiveClients) incrementResource(clientId int) {
	ac.mu.RLock()
	defer ac.mu.RUnlock()
	for _, existingClient := range ac.clients {
		if existingClient.id == clientId {
			existingClient.mu.Lock()
			defer existingClient.mu.Unlock()
			existingClient.resource.increment()
			return
		}
	}
}

type anError struct {
	errorMsg string
}

func (m anError) Error() string {
	return m.errorMsg
}

func (ac *ActiveClients) getResource(clientId int) (Resource, error) {
	ac.mu.RLock()
	defer ac.mu.RUnlock()

	for _, existingClient := range ac.clients {
		if existingClient.id == clientId {
			existingClient.mu.RLock()
			defer existingClient.mu.RUnlock()
			return existingClient.resource, nil
		}
	}
	return Resource{}, anError{"could not find resource"}
}

var wg sync.WaitGroup

func main() {
	var users ActiveClients
	users.add(&Client{id: 3})

	wg.Add(10)
	for i := 0; i < 10; i++ {
		go func() {
			users.incrementResource(3)
			wg.Done()
		}()
	}
	wg.Wait()

	res, err := users.getResource(3)
	if err != nil {
		fmt.Println(err)
	} else {
		fmt.Printf("resource data = %v\n", res.data)
	}
}

#include <iostream>
#include <vector>
#include <algorithm>
#include <set>

using namespace std;

vector<int> solve(vector<int> x, vector<int> y) {
    size_t n = x.size();
    vector<pair<pair<int, int>, int>> v(n);
    for (size_t i = 0; i < n; ++i) v[i] = {{y[i], x[i]}, i};
    sort(v.begin(), v.end());
    set<int> positions;
    for (size_t i = 1; i <= n; ++i) positions.insert(i);

    vector<int> ans(n);
    for (auto [pr, i] : v) {
        auto [r, l] = pr;
        set<int>::iterator it = positions.lower_bound(l);
        if (it == positions.end() || *it > r) return {};
        ans[i] = *it;
        positions.erase(it);
    }
    return ans;
}

int main() {
    ios_base::sync_with_stdio(0); cin.tie(0); cout.tie(0);
    int n; cin >> n;
    vector<int> a(n), b(n), c(n), d(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i] >> b[i] >> c[i] >> d[i];
    }
    vector<int> e = solve(a, c), f = solve(b, d);
    if (e.empty() || f.empty()) cout << "NIE\n";
    else {
        for (int i = 0; i < n; ++i) {
            cout << e[i] << ' ' << f[i] << '\n';
        }
    }
    return 0;
}

